import logo from './logo.svg';
import './App.css';

import React, { Component } from 'react'
import { getRandomColor } from './Utils';



var initialValues = [
  {
    value: 1,
    color: getRandomColor(),
  },
  {
    value: 2,
    color: getRandomColor(),
  },
  {
    value: 3,
    color: getRandomColor(),
  },
  {
    value: 4,
    color: getRandomColor(),
  },
  {
    value: 5,
    color: getRandomColor(),
  },
  {
    value: 6,
    color: getRandomColor(),
  },
  {
    value: 7,
    color: getRandomColor(),
  },
  {
    value: 8,
    color: getRandomColor(),
  },
  {
    value: 9,
    color: getRandomColor(),
  },
]


export default class App extends Component {

  state = {
    valueSelect: '',
    testSelect:'',
    tabValuesGame: [],
    tabValuesResult: [],
  }

  componentDidMount() {
    this.ClickNewGame()
  }
  ClickNewGame() {
    let tabGame = []
    let tabResult = []
    for (let index = 0; index < 9; index++) {
      var item = {
        value: index + 1,
        color: getRandomColor(),
      }
      tabGame.push(item)
      tabResult.push('')
    }
    this.setState({ tabValuesGame: tabGame, tabValuesResult: tabResult })
  }

  ClickNumber(item){
    this.setState({testSelect:item.value,valueSelect:item})
  }

  render() {
    let numberGameComponent = (item) => {
      return <div className=' btn text-center mb-2'
        style={{
          marginRight: "20px",
          height: "60px", width: "60px", fontSize: "30px",
          backgroundColor: item.color,
          border: (this.state.testSelect==item.value)? "4px dashed black":"",
        }} 
        onClick={()=>this.ClickNumber(item)}
        > {item.value} </div>
    }

    let numberResultComponent = (couleur, valeur) => {
      return <div id='result' className='result d-flex justify-content-center align-items-center rounded '
        style={{
          height: "100px", width: "100px", fontSize: "35px",
          backgroundColor: couleur,
          boxShadow: "0 0em 0.28em gray",
         // borderColor
         border: "4px dashed black",
          marginRight: "30px", marginBottom: "30px",
        }} > {valeur} </div>
    }

    return (

      <div className='container mt-3 px-4 '>
        <div className=' flex-row bg-warning'>
          <div style={{ fontSize: "20px" }} className='mr-4 float-start text-14'>
            123 Game
          </div>
          <button className='btn btn-primary btn-sm float-end' onClick={() => this.ClickNewGame()} > New game </button>
        </div>
        <br />
        <br />
        <div className='row'>
          <div className='col-md-6 col-xs-12 col-sm-12'>
            {
              this.state.tabValuesGame.map(item => {
                return numberGameComponent(item)
              })
            }
            <br/>
            {this.state.tabValuesGame.length} moves left.
          </div>
          <div className='col-md-5 col-12 col-sm-12 row'>
            {
              this.state.tabValuesResult.map(item => {
                return numberResultComponent(item.color, item.value)
              })
            }
          </div>
        </div>
      </div>
    )
  }
}
